import React, { useState, useEffect } from "react";
import axios from "axios";

const Interface = () => {
  const [city, setCity] = useState("");
  const [weather, setWeatherData] = useState(null);

  const fetchData = async () => {
    try {
      const { data } = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=adef24c0d24a95065818997a98ddd457`
      );
      setWeatherData(data);
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fetchData();
  }, [city]); // Fetch data whenever the city changes

  const handleInputChange = (e) => {
    setCity(e.target.value);
  };

  const handleGetWeatherClick = () => {
    fetchData();
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Enter city name"
        value={city}
        onChange={handleInputChange}
      />
      <button onClick={handleGetWeatherClick}>Get Weather</button>

      {weather ? (
        <div className="card">
          <h2>Location: {weather.name}</h2>
          <p>Temperature: {weather.main.temp}°C</p>
          <p>Feels like: {weather.main.feels_like}°C</p>
          <p>Description: {weather.weather[0].main}</p>
        </div>
      ) : (
        <p>Loading weather data...</p>
      )}
    </div>
  );
};

export default Interface;
