// ...

class Interface extends Component {
  state = { city: "" };

  onInput = (e) => {
    this.setState({ city: e.target.value });
  };

  onGetWeatherClick = async () => {
    try {
      await this.props.getWeatherData(this.state.city);
    } catch (error) {
      console.error("Error in onGetWeatherClick", error);
    }
  };

  render() {
    const { weather } = this.props;

    return (
      <div>
        <input
          type="text"
          placeholder="Enter city name"
          value={this.state.city}
          onChange={this.onInput}
        />
        <button onClick={this.onGetWeatherClick}>Get Weather</button>
        <>
          {weather && (
            <div className="card">
              <h2>Location: {weather.name}</h2>
              <p>Temperature: {weather.main.temp}</p>
              <p>Feels like: {weather.main.feels_like}</p>
              <p>Description: {weather.weather[0].main}</p>
            </div>
          )}
        </>
      </div>
    );
  }
}

export default Interface;

